import React, { useEffect, useCallback } from 'react';
import './UserViewScreen.scss';
import { Communications, DateConversion } from '../../../helpers';
import Paper from '@material-ui/core/Paper';
import Divider from '@material-ui/core/Divider';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import DialogComponent from "../../../components/core/DialogComponent";
import UserEditComponent from "../edit/UserEditComponent"
import CommonService from '../../../helpers/common-service';
import { ENV } from '../../../constants';
import { profile_default_logo } from "../../../constants/ImageConfig"
import { Button } from "@material-ui/core";
import { useTranslation } from 'react-i18next'; 

const UserViewScreen = () => {
    const params = useParams();
    const {t}=useTranslation()
    const { id }: any = params
    const [isAddOpen, setIsAddOpen] = React.useState<boolean>(false);
    const [userInfo, setUserInfo] = React.useState<any | null>(null)
    const role = useSelector((state: any) => state.auth.user.role);

    const init = useCallback(
        () => {
            CommonService._api.get(ENV.API_URL + 'member/' + id).then((resp) => {
                setUserInfo(resp.data);
            });
        },
        [id],
    )
    useEffect(() => {
        init();
        Communications.pageTitleSubject.next('Users List');
        Communications.pageBackButtonSubject.next('/users/list');
        Communications.accessRoleSubject.next('');
        Communications.accessRoleLinkSubject.next('');
    }, [id, init])

    const onReload = useCallback((page = 1) => {
        if (userInfo) {
            init()

        } else {
            setUserInfo((prevState: any) => {
                prevState?.reload(page);
                return prevState;
            })
        }
    }, [userInfo, init]);

    const openAdd = useCallback(() => {
        setIsAddOpen(true);
    }, [])

    const cancelAdd = useCallback(() => {
        setIsAddOpen(false);
        onReload(1);
    }, [onReload])

    const confirmAdd = useCallback(() => {
        setIsAddOpen(false);
        onReload(1);
    }, [onReload])

    return (
        <>
            <DialogComponent class={'dialog-side-wrapper'} open={isAddOpen} cancel={cancelAdd}>
                <UserEditComponent cancel={cancelAdd} confirm={confirmAdd} />
            </DialogComponent>
            <div className={'user-info'}>
                <div>
                <Paper className="paper d-flex">
                        <div className="user-details">
                            <div className="pdd-20">
                                <div className="d-flex">
                                    <div>
                                        {
                                            userInfo?.image_url ? <div className={'no-repeat-background user-profile-image'} style={{ backgroundImage: 'url(' + userInfo?.image_url + ')' }}>
                                            </div> : <img src={profile_default_logo} alt="" />
                                        }
                                    </div>
                                    <div>
                                        <p className="user-name">{userInfo?.name.length>20?userInfo?.name.slice(0,20)+"...":userInfo?.name}</p>
                                        <p className="hide over-flow">{userInfo?.name}</p>
                                    </div>
                                </div>
                                <div className="d-flex">
                                <div className="width-38">
                                        <p className="user-title">{t('Registered Date')}</p>
                                        <p className="patient-data">{DateConversion.dateConversion(userInfo?._created.slice(0, 10))} &nbsp;|&nbsp;  {new Date(userInfo?._created).toLocaleTimeString(undefined, {
                                            timeZone: 'Asia/Kolkata', hour12: true,
                                            hour: '2-digit',
                                            minute: '2-digit'
                                        })}</p>
                                    </div>
                                    <div className="user-id">
                                        <p className="user-title">{t('User Id')}</p>
                                        <p className="patient-data">UID-{userInfo?._id?.slice(19, 24)}</p>
                                    </div>
                                    <div className="width-25">
                                        <p className="user-title">{t('Date of Birth')}</p>
                                        <p className="patient-data">{DateConversion.dateConversion(userInfo?.dob)}</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="width-40 pdd-left-20 pdd-top-10">
                            <div className="d-flex">
                            <div className="width-50">
                                    <p >{t('User Type')}</p>
                                    <p className="user-type">{userInfo?.role === "admin" ? t("Admin") : t("Team Member")}</p>
                                </div>
                                <div className="width-50">
                                    <p >{t('Email')}</p>
                                    <p className="text-dark">{userInfo?.email ? userInfo?.email : "N/A"}</p>
                                </div>
                            </div>
                            <div className="d-flex">
                                <div className="width-50">
                                    <p >{t('Gender')}</p>
                                    <p className="text-dark">{t(userInfo?.gender)}</p>
                                </div>
                                <div className="width-50">
                                    <p >{t('Phone Number')}</p>
                                    <p className="text-dark">+91 &nbsp;{userInfo?.phone}</p>
                                </div>
                            </div>
                        </div>
                        <div className="width-40" style={{justifyContent:"center",alignItems:"center"}}>
                        {
                                role === "admin" ?
                            <Button variant={"contained"} color="secondary" className={"edit-button"} onClick={openAdd} id="btn_user_edit_profile">
                                {t('Edit Profile')}
                            </Button>:<></>}
                        </div>

                    </Paper>
                    <Paper className={'paper login-details-wrapper'}>
                    <div className={'basic-info'}>
                    <p>{t('Login Information')}</p>
                        </div>
                        <div className="d-flex users">
                            <div className={'d-flex users'} >
                                <div>
                                    <p>{t('Email')}</p>
                                    <p className={'text-dark'}>{userInfo?.email}</p>
                                </div>
                                <Divider className={'divider'} orientation="vertical" flexItem color={'primary'} />
                                <div>
                                    <p>{t('Password')}</p>
                                    <p className={'text-dark'}>********</p>
                                </div>
                            </div>
                        </div>
                    </Paper>
                </div>
            </div>
        </>
    );
};

export default UserViewScreen;
