import { Button, DialogActions, DialogContent, DialogTitle, InputAdornment } from '@material-ui/core';
import React, { PropsWithChildren, useState, useEffect } from 'react';
import * as Yup from "yup";
import { Field, Form, Formik, FormikHelpers } from "formik";
import FormLabel from "@material-ui/core/FormLabel";
import { TextField } from "formik-material-ui";
import { ENV } from "../../../constants";
import CommonService from "../../../helpers/common-service";
import "./UserEditComponent.scss"
import { useParams } from 'react-router-dom';
import { RadioGroup } from 'formik-material-ui';
import { FormControlLabel, Radio } from '@material-ui/core';
import {  useSelector } from 'react-redux';
import { useTranslation } from 'react-i18next';
 
const isPhone = new RegExp('(^[6789][0-9]{9})');

const formValidation = Yup.object({
    name: Yup.string().min(4, "Invalid name").test('alphabets', 'Name must only contain alphabets', (value: any) => {
        return /^[a-zA-Z0-9\s]+$/.test(value);
    }).required("Required"),
    email: Yup.string().email('Invalid email address').required('Required'),
    phone: Yup.string().min(10, 'Invalid Phone Number').max(10, 'Invalid Phone Number').matches(isPhone, 'Phone number is not valid').required('Required'),
})

export interface UserEditComponentProps {
    cancel: () => void,
    confirm: () => void,
}


const UserEditComponent = (props: PropsWithChildren<UserEditComponentProps>) => {
    const afterCancel = props?.cancel;
    const afterConfirm = props?.confirm;
    const params = useParams<{id: string}>();
    const {id} = params
    const [userInfo, setUserInfo] = useState<any>(null)
    const  user  = useSelector((state:any) => state.auth.user);
    const {t}=useTranslation()
    useEffect(() => {
        const init = () => {
            CommonService._api.get(ENV.API_URL + 'candidate/' + id).then((resp) => {
                console.log(resp.data)
                setUserInfo(resp.data);
            });
        }
        init();

    }, [id])
    
    const onAdd = (payload: any, { setSubmitting, setErrors }: FormikHelpers<any>) => {
        payload={
            ...payload,
            "user_id":userInfo?.user_id
        }
        CommonService._api.put(ENV.API_URL + 'candidate/' + id, payload).then((resp) => {
            // console.log(resp);
            setSubmitting(false);
            if (afterConfirm) {
                afterConfirm();
                CommonService.showToast(resp.msg || 'Success', 'success');
            }
        }).catch((err) => {
            CommonService.handleErrors(setErrors, err);
            setSubmitting(false);
        })
    }

    const cancel = () => {
        if (afterCancel) {
            afterCancel();
        }
    }

    return (
        <div className={"user-add"}>
            <DialogTitle id="alert-dialog-title">{t('Edit Candidate Details')}</DialogTitle>
            {
                userInfo && userInfo != null ?
                    <Formik initialValues={{ name: userInfo?.name,email: userInfo?.email, phone: userInfo?.phone, dob: userInfo?.dob, gender: userInfo?.gender, is_admin_access: userInfo?.role === "admin" ? true : false }} validateOnChange={true}
                        validationSchema={formValidation} onSubmit={onAdd}>
                        {({ isSubmitting, isValid }) => (<Form className={'form-holder'}>
                            <DialogContent>
                                <div className="form-field">
                                    <FormLabel className={'form-label'}>{t('Full Name')}*</FormLabel>
                                    <Field name='name' type={'text'} component={TextField} variant={"outlined"} id="input_user_edit_full_name"
                                        color={"primary"}
                                        autoComplete="off"   inputProps={{maxLength :100}}
                                        placeholder={'Enter Name'} />
                                </div>
                                <div className="form-field">
                                    <FormLabel className={'form-label'}>{t('Phone Number')}*</FormLabel>
                                    <Field name='phone' type={'text'} component={TextField} variant={"outlined"}
                                        color={"primary"}
                                        inputProps={{maxLength :10}}
                                        id="input_user_edit_phone"
                                        autoComplete="off"
                                        InputProps={{
                                            startAdornment: (
                                                <InputAdornment position="start">
                                                    +91
                                                </InputAdornment>
                                            ),
                                        }}
                                        placeholder={'Enter Phone Number'} />
                                </div>
                                <div className="form-field">
                                    <FormLabel className={'form-label'}>{t('Email ID')}*</FormLabel>
                                    <Field name='email' type={'email'} component={TextField} variant={"outlined"}
                                        color={"primary"}  inputProps={{maxLength :100}}
                                        autoComplete="off" 
                                        id="input_user_edit_email"
                                        placeholder={'Enter Email Address'} />
                                </div>
                        
                                <div className="form-field">
                                    <FormLabel className={'form-label'}>{t('Date of Birth')}*</FormLabel>
                                    <Field name='dob' type={"date"} component={TextField} variant={"outlined"}
                                        color={"primary"}
                                        autoComplete="off"
                                        id="input_user_edit_dob"
                                        placeholder={'Enter Date Of Birth'} />
                                </div>
                                <div className="form-field">
                            <FormLabel className={'form-label'}>{t('Gender')}*</FormLabel>
                            <div className={"d-flex form-label"}>
                                <div>
                                    <Field component={RadioGroup} name="gender" id="radio_user_edit_gender">
                                        <div className="d-flex ">
                                        <FormControlLabel
                                            value="Male"
                                            control={<Radio disabled={isSubmitting} />}
                                            label={t("Male")}
                                            className={"gender-div"}
                                            id="radio_user_gender_male"
                                            name="gender"
                                            disabled={isSubmitting}
                                        />
                                        <FormControlLabel
                                            value="Female"
                                            control={<Radio disabled={isSubmitting} />}
                                            label={t("Female")}
                                            id="radio_user_gender_female"
                                            name="gender"
                                            className={"gender-div "}
                                            disabled={isSubmitting}
                                        />
                                        <FormControlLabel
                                            value="Other"
                                            control={<Radio disabled={isSubmitting} />}
                                            label={t("Other")}
                                            name="gender"
                                            id="radio_user_gender_other"
                                            className={"gender-div "}
                                            disabled={isSubmitting}
                                        />
                                        </div>
                                    </Field>
                                </div>
                            </div>
                        </div>
                        {
                            user?._id===userInfo?.user_id ?<></>:<div className="form-field d-flex">
                            <div className="d-flex">
                        <FormLabel className={'form-label'}>{t("Give Admin Access")}</FormLabel>
                        <Field type="checkbox" name="is_admin_access" className="form-check-input" id="checkbox_user_edit_admin_access" />
                            </div>
                        </div>
                        }
                                
                            </DialogContent>
                            <DialogActions>
                                <Button onClick={cancel} color="secondary" id="btn_user_edit_cancel">
                                    {t('Cancel')}
                                </Button>
                                <Button type={"submit"} className={"submit"} disabled={isSubmitting || !isValid} variant={"contained"} color="secondary" id="btn_user_edit_submit">
                                    {t('Save Changes')}
                                </Button>
                            </DialogActions>
                        </Form>)}
                    </Formik>
                    : <></>}
        </div>
    )
};


export default UserEditComponent;
